import { ReactNode } from "react";
import { ClientTools } from "../types";

interface ToolingHandleProps {
  isStreaming: boolean;
  children: ReactNode;
}

interface ToolingHandleTriggerProps {
  title: string;
  status?: "processing" | "completed" | "error";
  toolName?: string;
  toolData: any;
  clientTools?: ClientTools;
}

interface ToolingHandleContentProps {
  children: ReactNode;
}

export function ToolingHandle({ children }: ToolingHandleProps) {
  return <div className="chat-wrapper__tooling-handle">{children}</div>;
}

