  if (toolName?.startsWith("lat_")) {
    const query = toolData?.parameters?.query;
    const url = toolData?.parameters?.url; // Fixed: added 's' to 'parameters'
    toolDescription = query || url || "Executing tool...";
  } else {
    toolDescription = getToolDescription();
  }
